import { useState } from "react";
import "./App.css";

function App() {
  const[count,setCount] = useState(0);

  function decrease(){
   setCount(count-1);
  }
  function increase(){
    setCount(count+1);
   }
   function reset(){
    setCount(0);
   }

  return (
    <div className="w-[100vw] h-[100vh] flex justify-center items-center bg-[#344151] flex-col gap-10">
      <div className="text-[#0398d4] font-medium text-2xl">Increment & Decrement</div>
    <div className="bg-white flex justify-center gap-12 py-3 rounded-sm text-[25px] text-[#344151]">
      <button onClick={decrease} className="border-r-2 text-center w-20 border-[#bfbfbf] text-5xl">
        -</button>
      </div> 
      <div className="font-bold gap-12 text-5xl">{count}</div>
    <div>
      <button onClick={increase} className="border-r-2 text-center w-20 border-[#bfbfbf] text-5xl">
        +</button>
    </div>
    <div>
      <button onClick={reset} className="bg-[#0398d4] text-white px-5 py-2 rounded-sm text-lg">
        Reset</button>
    </div>
    </div>
  );
}

export default App;
